let root = $json.body ?? $json;
if (typeof root === "string") {
  try { root = JSON.parse(root); } catch { root = {}; }
}

let hist;
if (root && root.status) {
  hist = root;
} else {
  const id = Object.keys(root || {})[0];
  hist = root?.[id] || {};
}

// 2) Pull prompt object and find checker node id
let promptObj = hist.prompt;
if (Array.isArray(promptObj)) promptObj = promptObj[2];

let checkerId = null;
if (promptObj && typeof promptObj === "object") {
  for (const [nid, node] of Object.entries(promptObj)) {
    if (node?.class_type === "YetAnotherSafetyChecker") {
      checkerId = nid;
      break;
    }
  }
}

// 3) Look for checker outputs (may be null if not run)
let label = null;
if (checkerId && hist.outputs && hist.outputs[checkerId]) {
  const o = hist.outputs[checkerId];

  // try common keys
  for (const k of ["string", "text", "label", "status", "message", "STRING"]) {
    if (k in o) {
      const v = Array.isArray(o[k]) ? o[k][0] : o[k];
      if (typeof v === "string" && v.trim()) { label = v.trim(); break; }
    }
  }
  if (!label && Array.isArray(o.output) && typeof o.output[1] === "string") {
    label = o.output[1].trim();
  }
}

// 4) Decide if blocked (only true if label says unsafe/nsfw/blocked)
const blocked = !!(label && /unsafe|nsfw|blocked/i.test(label));

return [{
  ...$json,
  hist,
  checkerId,
  label: label ?? null,
  blocked
}];

