// Input contract:
// $json.comfyUrl   â†’ base URL (trimmed later), optional
// $json.body       â†’ full /prompt response or history blob (object or stringified)
// Falls back to https://comfyui.codemusic.ca if comfyUrl missing

// 1) comfyUrl (no external node refs)
const comfyUrl = String(($json.comfyUrl || 'https://comfyui.codemusic.ca')).replace(/\/+$/,'');

// 2) normalize "root" from $json.body (accept string or object)
let root = $json.body ?? $json;
if (typeof root === 'string') { try { root = JSON.parse(root); } catch { root = {}; } }

// 3) some history payloads wrap under the first key; unwrap if needed
if (!root.status && typeof root === 'object') {
  const k = Object.keys(root || {})[0];
  if (k && root[k] && typeof root[k] === 'object' && 'status' in root[k]) {
    root = root[k];
  }
}

// 4) handle graph location (some APIs return [ok, msg, prompt])
let P = root.prompt;
if (Array.isArray(P)) P = P[2];

// 5) discover nodes
let checkerId = null, saveIds = [];
for (const [nid, node] of Object.entries(P || {})) {
  if (node?.class_type === 'YetAnotherSafetyChecker') checkerId = nid;
  if (node?.class_type === 'SaveImage') saveIds.push(nid);
}

// 6) pick images from outputs (prefer SaveImage ids, else first images array found)
const outs = root.outputs || {};
let imgs = [];
for (const sid of saveIds) {
  const arr = outs?.[sid]?.images;
  if (Array.isArray(arr) && arr.length) { imgs = arr; break; }
}
if (!imgs.length) {
  for (const v of Object.values(outs)) {
    if (Array.isArray(v?.images) && v.images.length) { imgs = v.images; break; }
  }
}
const img = imgs[0] || {};
const filename = img.filename || '';
const subfolder = img.subfolder || '';
const type = img.type || 'output';

// 7) read safety checker label â†’ blocked?
let label = null;
if (checkerId && outs[checkerId]) {
  const o = outs[checkerId];
  for (const k of ['string','text','label','status','message']) {
    if (k in o) {
      const v = Array.isArray(o[k]) ? o[k][0] : o[k];
      if (typeof v === 'string' && v.trim()) { label = v.trim(); break; }
    }
  }
  // some builds use o.output = [tensor, "label"]
  if (!label && Array.isArray(o.output) && typeof o.output[1] === 'string') {
    label = o.output[1].trim();
  }
}
const blocked = !!(label && /unsafe|nsfw|blocked/i.test(label));

// 8) view URL for convenience
const viewUrl = filename
  ? `${comfyUrl}/view?filename=${encodeURIComponent(filename)}&subfolder=${encodeURIComponent(subfolder)}&type=${encodeURIComponent(type)}`
  : null;

return [{
  json: {
    hist: root,
    checkerId,
    saveIds,
    filename, subfolder, type,
    label: label ?? null,
    blocked,
    comfyUrl,
    viewUrl
  }
}];

const items = $input.all();
return items.map(item => {
  const bin = item.binary?.img;
  if (!bin?.data) {
    return { json: { ...item.json, censored: null, blocked: Boolean(item.json?.blocked), reason: "no_binary" }, binary: item.binary || {} };
  }
  const buf = bin.data;
  const tiny = buf.length < 4096;

  const start = Math.min(2048, Math.max(0, buf.length - 1));
  const end   = Math.min(buf.length, start + 8192);
  const span  = Math.max(0, end - start);
  const step  = Math.max(1, Math.floor((span || 1) / 512));

  let n = 0, mean = 0, M2 = 0;
  const uniq = new Set();
  for (let i = start; i < end; i += step) {
    const x = (buf[i] & 0xff);
    uniq.add(x);
    n++;
    const d = x - mean;
    mean += d / n;
    M2   += d * (x - mean);
  }
  const variance = n > 1 ? (M2 / (n - 1)) : 0;

  const veryLowVar = variance < 2;
  const lowUnique  = uniq.size <= 4;
  const censored   = tiny || (veryLowVar && lowUnique);
  const blocked    = Boolean(item.json?.blocked) || censored;

  return {
    json: { ...item.json, censored, blocked, debug: { size: buf.length, variance, unique: uniq.size, step, start } },
    binary: item.binary
  };
});

// Webhook1 body fields: base64Image, mimeType, fileName
function padB64(s){const r=s.length%4; if(r===0)return s; if(r===2)return s+"=="; if(r===3)return s+"="; throw new Error("Invalid base64 length (mod 4 = 1)");}
function stripDataUrl(s){const m=/^data:([^;]+);base64,(.*)$/i.exec(s); return m?{mime:m[1],b64:m[2]}:{mime:null,b64:s};}
function detectMime(buf){
  if(buf.length>=8 && buf[0]===0x89&&buf[1]===0x50&&buf[2]===0x4E&&buf[3]===0x47&&buf[4]===0x0D&&buf[5]===0x0A&&buf[6]===0x1A&&buf[7]===0x0A) return "image/png";
  if(buf.length>=3 && buf[0]===0xFF&&buf[1]===0xD8&&buf[2]===0xFF) return "image/jpeg";
  if(buf.length>=12 && buf[0]===0x52&&buf[1]===0x49&&buf[2]===0x46&&buf[3]===0x46 && buf[8]===0x57&&buf[9]===0x45&&buf[10]===0x42&&buf[11]===0x50) return "image/webp";
  return "application/octet-stream";
}
function extForMime(m){return m==="image/png"?".png":m==="image/jpeg"?".jpg":m==="image/webp"?".webp":".bin";}

let raw = $json?.body?.base64Image;
let mime = $json?.body?.mimeType || null;
let fileName = $json?.body?.fileName || "upload";

if (typeof raw !== "string" || !raw.trim()) throw new Error("Missing body.base64Image");

const { mime: fromURL, b64 } = stripDataUrl(raw.trim());
if (fromURL && !mime) mime = fromURL;

let cleaned = b64.replace(/\s+/g,"").replace(/-/g,"+").replace(/_/g,"/");
cleaned = padB64(cleaned);

const buf = Buffer.from(cleaned, "base64");
if (!buf.length) throw new Error("Decoded buffer empty");

// sniff mime if missing/generic
const sniff = detectMime(buf);
if (!mime || mime === "application/octet-stream") mime = sniff;

// normalize extension
const wantExt = extForMime(mime);
if (!fileName.toLowerCase().endsWith(wantExt)) {
  fileName = fileName.replace(/\.[a-z0-9]+$/i,"") + wantExt;
}

// âž• make filename unique (timestamp + short random suffix)
const ts = new Date().toISOString().replace(/[-:.TZ]/g,""); // e.g. 20250913T162355
const rand = Math.floor(Math.random() * 1e6).toString().padStart(6,"0");
const uniqueFileName = fileName.replace(/\.[^.]+$/, `_${ts}_${rand}$&`);

// debug magic
const magicHex = [...buf.subarray(0,12)].map(b=>b.toString(16).padStart(2,"0")).join(" ");

return {
  json: {
    ...$json,
    base64: cleaned,
    dataUrl: `data:${mime};base64,${cleaned}`,
    fileName: uniqueFileName,        // ðŸ”¥ use unique name downstream
    mimeType: mime,
    _decodedSize: buf.length,
    _magic: magicHex
  },
  binary: {
    data: { data: buf.toString("base64"), fileName: uniqueFileName, mimeType: mime }
  }
};
