// ==============================
// Pre-Fusion: prompt + agents' finals only
// ==============================

// Safe access (prevents ExpressionError if node missing/not executed)
const safeGet = (fn, fallback) => {
  try {
    const v = fn();
    return (v ?? "") === "" ? fallback : v;
  } catch {
    return fallback;
  }
};

// Prompt priority: SetChatInput ‚Üí SetChatInput_forSMS ‚Üí Edit Fields ‚Üí "No prompt"
const prompt = safeGet(
  () => $node["SetChatInput"].json.query,
  safeGet(
    () => $node["SetChatInput_forSMS"].json.query,
    safeGet(() => $node["Edit Fields"].json.query, "No prompt")
  )
);

// Source responses: expecting items[0].json.data = array
const raw = (items?.[0]?.json?.data) || [];

// Normalize and trim out <think>...</think>
const toFinal = (v) => {
  const s = (v?.output ?? v?.text ?? v ?? "").toString();
  return s.replace(/<think>[\s\S]*?<\/think>/gi, "").trim();
};

// Build agent_responses (finals only). Keep whatever number is present (1‚ÄìN).
const agent_responses = raw.map((r, i) => ({
  agent: `Agent ${i + 1}`,
  output: toFinal(r),
}));

return [{
  json: {
    prompt,
    agent_responses
  }
}];

// ==============================
// Post-Fusion: Structured JSON output (single thought)
// ==============================

const safeGet = (fn, fb) => { 
  try { 
    const v = fn(); 
    return (v ?? "") === "" ? fb : v; 
  } catch { 
    return fb; 
  } 
};

// Read agents from the exact SetAgentRespones node
const saAgents  = safeGet(() => $node["SetAgentRespones"].json.agent_responses, []);

// Pull raw outputs (or "")
const agent1Raw = (Array.isArray(saAgents) && saAgents[0]?.output) || "";
const agent2Raw = (Array.isArray(saAgents) && saAgents[1]?.output) || "";

// Pull final fused output
const finalRaw =
  ($json?.final) ??
  ($json?.output) ??
  ($json?.text) ??
  ($json?.data?.output) ??
  "";

// Extract thought: <think> if exists, else whole text
const extractThought = (text) => {
  const match = (text || "").toString().match(/<think>([\s\S]*?)<\/think>/i);
  return match ? match[1].trim() : text.trim();
};

// Remove <think> section for final response
const stripThink = (text) => (text || "").toString().replace(/<think>[\s\S]*?<\/think>/gi, "").trim();

// Build structured POV array
const pov = [];

if (agent1Raw) {
  pov.push({
    name: saAgents[0]?.agent || "Agent 1",
    type: "logical",
    thought: extractThought(agent1Raw)
  });
}

if (agent2Raw) {
  pov.push({
    name: saAgents[1]?.agent || "Agent 2",
    type: "creative",
    thought: extractThought(agent2Raw)
  });
}

// Return final JSON for downstream parsing
return [{
  json: {
    pov,
    response: stripThink(finalRaw)
  }
}];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

// Mock one item that matches your future webhook shape
return [{
  json: {
    data: {
      from: "+16135551234",
      fromCity: "OTTAWA",
      fromState: "ON",
      fromCountry: "CA",
      timestamp: "2025-08-18T20:06:47.722Z",
      body: "Hey Musai, make me laugh!"
    }
  }
}];

// Get input data
const input = $json;

// Clean up the prompt
const rawQuery = input.query || '';
const prompt = rawQuery
  .trim()
  .replace(/^\n+|\n+$/g, ''); // remove leading/trailing newlines

return [
  {
    json: {
      prompt,
      agent_responses: [
        {
          agent: "Agent 1",
          output: ""
        },
        {
          agent: "Agent 2",
          output: ""
        }
      ]
    }
  }
];

// Force Eastern Time (Toronto)
const TZ = 'America/Toronto';
const now = new Date();

// Time hh:mm AM/PM
const time12 = new Intl.DateTimeFormat('en-US', {
  timeZone: TZ, hour: 'numeric', minute: '2-digit', hour12: true,
}).format(now);

// Date "MMM dd{suffix} YYYY"
const parts = new Intl.DateTimeFormat('en-US', {
  timeZone: TZ, month: 'short', day: 'numeric', year: 'numeric',
}).formatToParts(now);

const get = (t) => parts.find(p => p.type === t)?.value || '';
const month = get('month');
const dayNum = Number(get('day'));
const year = get('year');
const suffix = (d) => (d > 3 && d < 21) ? 'th'
  : (['th','st','nd','rd'][Math.min(d % 10, 4)] || 'th');
const datePretty = `${month} ${dayNum}${suffix(dayNum)} ${year}`;

// Weekday + part of day
const dow = new Intl.DateTimeFormat('en-US', { timeZone: TZ, weekday: 'long' }).format(now);
const hourET = Number(new Intl.DateTimeFormat('en-US', { timeZone: TZ, hour: 'numeric', hour12: false }).format(now));
const partOfDay = hourET < 5 ? 'late night' : hourET < 12 ? 'morning' : hourET < 17 ? 'afternoon' : 'evening';

// Zenith daily focus map
const ZENITH = {
  Sunday:    { color: 'red',    chakra: 'root',         virtue: 'chastity',   shadow: 'lust',     note: 'C', line: 'Ground in red, root strength; chastity steadies, note C anchors.' },
  Monday:    { color: 'orange', chakra: 'sacral',       virtue: 'temperance', shadow: 'gluttony', note: 'D', line: 'Orange sacral flow; temperance balances, note D inspires creativity.' },
  Tuesday:   { color: 'yellow', chakra: 'solar plexus', virtue: 'charity',    shadow: 'greed',    note: 'E', line: 'Yellow solar fire; charity empowers, note E strengthens will.' },
  Wednesday: { color: 'green',  chakra: 'heart',        virtue: 'diligence',  shadow: 'sloth',    note: 'F', line: 'Green heart glow; diligence deepens love, note F harmonizes bonds.' },
  Thursday:  { color: 'blue',   chakra: 'throat',       virtue: 'forgiveness',shadow: 'wrath',    note: 'G', line: 'Blue throat truth; forgiveness softens, note G clears expression.' },
  Friday:    { color: 'indigo', chakra: 'third eye',    virtue: 'kindness',   shadow: 'envy',     note: 'A', line: 'Indigo vision; kindness opens intuition, note A heightens clarity.' },
  Saturday:  { color: 'purple', chakra: 'crown',        virtue: 'humility',   shadow: 'pride',    note: 'B', line: 'Purple crown light; humility invites wisdom, note B uplifts spirit.' },
};

function zenithSentence(dayName) {
  const z = ZENITH[dayName] || ZENITH.Saturday;
  return `Daily focus ‚Üí ${z.line}`;
}

// Concise reference
const ZENITH_REF = `
[Zenith Framework]
A daily map linking color, chakra, virtue, shadow, and musical note.
Virtue balances its shadow. One focus per day builds steady growth.
`.trim();

return items.map(item => {
  const src = item.json?.body ?? item.json ?? {};
  const sessionId   = src.sessionId ?? null;
  const action      = src.action ?? null;
  const meta        = src.meta ?? null;
  const perspective = src.perspective ?? null;
  const query       = src.query ?? '';

  const contextBlock = `[Subtle Context: ${partOfDay}, ${dow}, ${datePretty} at ${time12}. ${zenithSentence(dow)}]`;

  item.json = {
    sessionId,
    query: `
${contextBlock}

${ZENITH_REF}

User Query: ${query}
`.trim(),
    action,
    meta,
    perspective
  };

  return item;
});

